-- 检查插入结果
SELECT 
    type, 
    color_customizable,
    COUNT(*) as count,
    AVG(height) as avg_height,
    MIN(height) as min_height,
    MAX(height) as max_height
FROM characters 
WHERE type = 'generic'
GROUP BY type, color_customizable
ORDER BY color_customizable;

-- 显示所有generic角色概览
SELECT id, name, height, color, color_customizable, "order" FROM characters WHERE type = 'generic' ORDER BY "order", name;