-- 检查插入结果
SELECT 
    type, 
    COUNT(*) as count,
    AVG(height) as avg_height,
    MIN(height) as min_height,
    MAX(height) as max_height
FROM characters 
WHERE type = 'generic'
GROUP BY type;

-- 显示所有generic角色
SELECT id, name, height, color_customizable FROM characters WHERE type = 'generic' ORDER BY height, name;